#include <iostream>\n#include <vector>\n#include <string>\n#include <cassert>\n\n// Test includes\n#include \"../include/batch_introspection_command.h\"\n#include \"../include/batch_artifact_loader.h\"\n#include \"../include/cli_types.h\"\n\nusing namespace nx::cli;\n\nvoid test_batch_introspection_help() {\n    std::cout << \"Testing batch introspection help...\\n\";\n    \n    std::vector<std::string> help_args = {\"--help\"};\n    auto result = BatchIntrospectionCommand::execute(help_args);\n    \n    assert(result.success);\n    assert(result.error_code == CliErrorCode::NONE);\n    \n    std::cout << \"✓ Help command works\\n\";\n}\n\nvoid test_unknown_subcommand() {\n    std::cout << \"Testing unknown subcommand handling...\\n\";\n    \n    std::vector<std::string> invalid_args = {\"unknown_command\"};\n    auto result = BatchIntrospectionCommand::execute(invalid_args);\n    \n    assert(!result.success);\n    assert(result.error_code == CliErrorCode::NX_CLI_USAGE_ERROR);\n    \n    std::cout << \"✓ Unknown subcommand properly rejected\\n\";\n}\n\nvoid test_artifact_loader_validation() {\n    std::cout << \"Testing artifact loader input validation...\\n\";\n    \n    BatchPlanArtifact plan;\n    auto result = BatchArtifactLoader::load_batch_plan(\"\", plan);\n    \n    assert(!result.success);\n    assert(result.error_code == CliErrorCode::NX_CLI_USAGE_ERROR);\n    \n    std::cout << \"✓ Empty batch ID properly rejected\\n\";\n}\n\nvoid test_error_codes() {\n    std::cout << \"Testing Phase 14A error codes...\\n\";\n    \n    // Verify new error codes are available\n    CliErrorCode batch_not_found = CliErrorCode::ERROR_BATCH_NOT_FOUND;\n    CliErrorCode job_not_found = CliErrorCode::ERROR_JOB_NOT_FOUND;\n    CliErrorCode artifact_not_found = CliErrorCode::ERROR_ARTIFACT_NOT_FOUND;\n    CliErrorCode execution_incomplete = CliErrorCode::ERROR_EXECUTION_INCOMPLETE;\n    \n    // Basic enum value checks\n    assert(batch_not_found != CliErrorCode::NONE);\n    assert(job_not_found != CliErrorCode::NONE);\n    assert(artifact_not_found != CliErrorCode::NONE);\n    assert(execution_incomplete != CliErrorCode::NONE);\n    \n    std::cout << \"✓ Phase 14A error codes available\\n\";\n}\n\nint main() {\n    std::cout << \"=== Phase 14A.2 Foundation Tests ===\\n\\n\";\n    \n    try {\n        test_batch_introspection_help();\n        test_unknown_subcommand();\n        test_artifact_loader_validation();\n        test_error_codes();\n        \n        std::cout << \"\\n✅ All foundation tests passed\\n\";\n        std::cout << \"Phase 14A.2 foundation is ready for command implementation\\n\";\n        \n        return 0;\n    } catch (const std::exception& e) {\n        std::cerr << \"❌ Test failed: \" << e.what() << std::endl;\n        return 1;\n    }\n}